-------- ACTIONS  ----------
if(SERVER) then
  asmlib.SetAction("DUPE_PHYS_SETTINGS",
    function(oPly,oEnt,tData) -- Duplicator wrapper
      if(not asmlib.ApplyPhysicalSettings(oEnt,tData[1])) then
        return asmlib.StatusLog(false,"DUPE_PHYS_SETTINGS: Failed to apply physical settings on "..tostring(oEnt))
      end
      return asmlib.StatusLog(true,"DUPE_PHYS_SETTINGS: Success")
    end)
end

if(CLIENT) then
  asmlib.SetAction("RESET_OFFSETS",
    function(oPly,oCom,oArgs)
      oPly:ConCommand(gsToolPrefL.."nextx 0\n")
      oPly:ConCommand(gsToolPrefL.."nexty 0\n")
      oPly:ConCommand(gsToolPrefL.."nextz 0\n")
      oPly:ConCommand(gsToolPrefL.."nextpic 0\n")
      oPly:ConCommand(gsToolPrefL.."nextyaw 0\n")
      oPly:ConCommand(gsToolPrefL.."nextrol 0\n")
      return asmlib.StatusLog(true,"RESET_OFFSETS: Success")
    end)

  asmlib.SetAction("OPEN_FRAME",
    function(oPly,oCom,oArgs)
      local frUsed, nCount = asmlib.GetFrequentModels(oArgs[1])
      if(not asmlib.IsExistent(frUsed)) then
        return asmlib.StatusLog(false,"OPEN_FRAME: Failed to retrieve most frequent models ["..tostring(oArgs[1]).."]")
      end
      local defTable = asmlib.GetOpVar("DEFTABLE_PIECES")
      if(not defTable) then return StatusLog(false,"Missing definition for table PIECES") end
      local pnFrame = vguiCreate("DFrame")
      if(not IsValid(pnFrame)) then
        pnFrame:Remove()
        return asmlib.StatusLog(false,"OPEN_FRAME: Failed to create base frame")
      end
      local pnElements = asmlib.MakeContainer("FREQ_VGUI")
            pnElements:Insert(1,{Label = { "DButton"    ,"Export DB"  ,"Click to export the client database as a file"}})
            pnElements:Insert(2,{Label = { "DListView"  ,"ItemRoutine","The list of your frequently used track pieces"}})
            pnElements:Insert(3,{Label = { "DModelPanel","ItemScreen" ,"The model of your track piece is displayed here"}})
            pnElements:Insert(4,{Label = { "DTextEntry" ,"ItemSearch" ,"Enter a pattern here and hit enter to preform a search"}})
            pnElements:Insert(5,{Label = { "DComboBox"  ,"StatSearch" ,"Choose which list column you want to preform a search on"}})
      ------------ Manage the invalid panels -------------------
      local iNdex, iSize, vItem = 1, pnElements:GetSize(), nil
      while(iNdex <= iSize) do
        vItem = pnElements:Select(iNdex)
        vItem.Panel = vguiCreate(vItem.Label[1],pnFrame)
        if(not IsValid(vItem.Panel)) then
          asmlib.LogInstance("OPEN_FRAME: Failed to create ID #"..iNdex)
          iNdex, vItem = 1, nil
          while(iNdex <= iSize) do
            vItem = pnElements:Select(iNdex)
            if(IsValid(vItem.Panel)) then
              vItem.Panel:Remove()
              asmlib.LogInstance("OPEN_FRAME: Deleted panel ID #"..iNdex)
            end
            pnElements:Delete(iNdex)
            asmlib.LogInstance("OPEN_FRAME: Deleted entry ID #"..iNdex)
            iNdex = iNdex + 1
          end
          pnFrame:Remove()
          collectgarbage()
          return StatusLog(false,"OPEN_FRAME: Invalid panel created. Frame removed")
        end
        vItem.Panel:SetName(vItem.Label[2])
        vItem.Panel:SetTooltip(vItem.Label[3])
        iNdex = iNdex + 1
      end
      ------ Screen resolution and elements -------
      local scrW = surfaceScreenWidth()
      local scrH = surfaceScreenHeight()
      local pnButton     = pnElements:Select(1).Panel
      local pnListView   = pnElements:Select(2).Panel
      local pnModelPanel = pnElements:Select(3).Panel
      local pnTextEntry  = pnElements:Select(4).Panel
      local pnComboBox   = pnElements:Select(5).Panel
      ------------ Frame --------------
      pnFrame:SetTitle("Frequent pieces by "..oPly:GetName().." (Ver."..asmlib.GetOpVar("TOOL_VERSION")..")")
      pnFrame:SetVisible(true)
      pnFrame:SetDraggable(true)
      pnFrame:SetDeleteOnClose(true)
      pnFrame:SetPos(scrW/4, scrH/4)
      pnFrame:SetSize(750, 280)
      pnFrame.OnClose = function()
        pnFrame:SetVisible(false)
        local iNdex, iSize = 1, pnElements:GetSize()
        while(iNdex <= iSize) do -- All panels are valid
          asmlib.LogInstance("OPEN_FRAME: Frame.OnClose: Delete #"..iNdex)
          pnElements:Select(iNdex).Panel:Remove()
          pnElements:Delete(iNdex)
          iNdex = iNdex + 1
        end
        pnFrame:Remove()
        collectgarbage()
        asmlib.LogInstance("OPEN_FRAME: Frame.OnClose: Form removed")
      end
      ------------ ModelPanel --------------
      pnModelPanel:SetParent(pnFrame)
      pnModelPanel:SetPos(500,25)
      pnModelPanel:SetSize(250,255)
      pnModelPanel:SetVisible(true)
      pnModelPanel.LayoutEntity = function(pnSelf, oEnt)
        if(pnSelf.bAnimated) then pnSelf:RunAnimation() end
        local uiRec = asmlib.CacheQueryPiece(oEnt:GetModel())
        if(not uiRec) then return end
        local uiKept = uiRec.Kept
        local uiAng = Angle(0, RealTime() * 10, 0)
        local uiPos = Vector()
        if(uiKept > 1) then
          local uiCalc = asmlib.GetCenterPoint(uiRec,"P")
          if(not asmlib.IsExistent(uiCalc)) then return asmlib.StatusLog(false,"OPEN_FRAME: ModelPanel.LayoutEntity: Center point non-applicable") end
          asmlib.SetVector(uiPos,uiCalc)
        elseif(uiKept == 1) then
          local uiMin, uiMax = oEnt:GetRenderBounds()
          asmlib.SetVector(uiPos,uiMax)
          asmlib.SubVector(uiPos,uiMin)
          uiPos:Mul(-0.5)
        else return asmlib.StatusLog(false,"OPEN_FRAME: ModelPanel.LayoutEntity: Record has no points") end
        local uiRot = Vector()
              uiRot:Set(uiPos)
              uiRot:Rotate(uiAng)
              uiRot:Mul(-1)
              uiRot:Add(uiPos)
        oEnt:SetAngles(uiAng)
        oEnt:SetPos(uiRot)
      end
      ------------ Button --------------
      pnButton:SetParent(pnFrame)
      pnButton:SetText(pnElements:Select(1).Label[2])
      pnButton:SetPos(15,30)
      pnButton:SetSize(55,30)
      pnButton:SetVisible(true)
      pnButton.DoClick = function()
        asmlib.LogInstance("OPEN_FRAME: Button.DoClick: <"..pnButton:GetText().."> clicked")
        asmlib.SetLogControl(asmlib.GetCoVar("logsmax", "INT"),
                             asmlib.GetCoVar("logfile", "STR"))
        local ExportDB     = asmlib.GetCoVar("exportdb","INT")
        if(ExportDB ~= 0) then
          asmlib.LogInstance("OPEN_FRAME: Button Exporting DB")
          asmlib.ExportIntoFile("PIECES",",","INS")
          asmlib.ExportIntoFile("ADDITIONS",",","INS")
          asmlib.ExportIntoFile("PHYSPROPERTIES",",","INS")
          asmlib.ExportIntoFile("PIECES","\t","DSV")
          asmlib.ExportIntoFile("ADDITIONS","\t","DSV")
          asmlib.ExportIntoFile("PHYSPROPERTIES","\t","DSV")
        end
      end
      ------------ ListView --------------
      pnListView:SetParent(pnFrame)
      pnListView:SetVisible(false)
      pnListView:SetSortable(true)
      pnListView:SetMultiSelect(false)
      pnListView:SetPos(10,65)
      pnListView:SetSize(480,205)
      pnListView:AddColumn("Used"):SetFixedWidth(55)  -- (1)
      pnListView:AddColumn("Act"):SetFixedWidth(20)   -- (2)
      pnListView:AddColumn("Type"):SetFixedWidth(100) -- (2)
      pnListView:AddColumn("Model"):SetFixedWidth(305)-- (4)
      pnListView.OnRowSelected = function(pnSelf, nIndex, pnLine)
        local uiMod = pnLine:GetColumnText(4) -- Forth index is actually the model in the table
        local uiRec = asmlib.CacheQueryPiece(uiMod)
        if(not asmlib.IsExistent(uiRec)) then
          return asmlib.StatusLog(false,"OPEN_FRAME: ListView.OnRowSelected: Failed to retrieve #"..nIndex.." model <"..uiMod..">")
        end
        pnModelPanel:SetModel(uiMod) -- Set the damn thing only if valid record is found
        -- OBBCenter ModelPanel Configuration --
        local uiEnt = pnModelPanel:GetEntity()
        if(not (uiEnt and uiEnt:IsValid())) then
          return asmlib.StatusLog(false,"OPEN_FRAME: ListView.OnRowSelected: Model panel entity invalid")
        end
        local uiKept = tonumber(uiRec.Kept) or 0
        local uiCen  = Vector()
        if(uiKept > 1) then
          local uiCalc = asmlib.GetCenterPoint(uiRec,"P")
          if(not asmlib.IsExistent(uiCalc)) then return asmlib.StatusLog(false,"OPEN_FRAME: ListView.OnRowSelected: Center point non-applicable") end
          asmlib.SetVector(uiCen,uiCalc)
        elseif(uiKept == 1) then
          local uiMin, uiMax = uiEnt:GetRenderBounds()
          asmlib.SetVector(uiCen,uiMax)
          asmlib.SubVector(uiCen,uiMin)
          uiCen:Mul(-0.5)
        else return asmlib.StatusLog(false,"OPEN_FRAME: ListView.OnRowSelected: Record has no points") end
        local uiEye = uiEnt:LocalToWorld(uiCen)
        asmlib.SubVector(uiCen,uiRec.Offs[1].P)
        local uiLen = uiCen:Length()
        local uiCam = Vector(0.70 * uiLen, 0, 0.30 * uiLen)
        pnModelPanel:SetLookAt(uiEye)
        pnModelPanel:SetCamPos(2 * uiCam + uiEye)
        oPly:ConCommand(gsToolPrefL.."model "..uiMod.."\n")
        oPly:ConCommand(gsToolPrefL.."pointid 1\n")
        oPly:ConCommand(gsToolPrefL.."pnextid 2\n")
      end
      if(not asmlib.UpdateListView(pnListView,frUsed,nCount)) then
        asmlib.StatusLog(false,"OPEN_FRAME: ListView.OnRowSelected: Populate the list view failed")
      end
      ------------- ComboBox ---------------
      pnComboBox:SetParent(pnFrame)
      pnComboBox:SetPos(75,30)
      pnComboBox:SetSize(95,30)
      pnComboBox:SetVisible(true)
      pnComboBox:SetValue("<Search BY>")
      pnComboBox:AddChoice("Model",defTable[1][1])
      pnComboBox:AddChoice("Type" ,defTable[2][1])
      pnComboBox:AddChoice("Name" ,defTable[3][1])
      pnComboBox:AddChoice("Act"  ,defTable[4][1])
      pnComboBox.OnSelect = function(pnSelf, nInd, sVal, anyData)
        asmlib.LogInstance("OPEN_FRAME: ComboBox.OnSelect: ID #"..nInd.."<"..sVal..">"..tostring(anyData))
        pnSelf:SetValue(sVal)
      end
      ------------ TextEntry --------------
      pnTextEntry:SetParent(pnFrame)
      pnTextEntry:SetPos(175,30)
      pnTextEntry:SetSize(330,30)
      pnTextEntry:SetVisible(true)
      pnTextEntry.OnEnter = function(pnSelf)
        local sName, sField = pnComboBox:GetSelected()
              sName    = tostring(sName  or "")
              sField   = tostring(sField or "")
        local sPattern = tostring(pnSelf:GetValue() or "")
        if(not asmlib.UpdateListView(pnListView,frUsed,nCount,sField,sPattern)) then
          return asmlib.StatusLog(false,"OPEN_FRAME: TextEntry.OnEnter: Failed to update ListView {"..sName.."#"..sField.."#"..sPattern.."}")
        end
      end
      ------------ Show the completed panel --------------
      pnFrame:SetVisible(true)
      pnFrame:Center()
      pnFrame:MakePopup()
      collectgarbage()
      return asmlib.StatusLog(true,"OPEN_FRAME: Success")
    end)
end

